mapper.select:
SELECT *, IF(io_input = '1', '매입','매출') AS io_inout, IF(io_input = '1', io_price, 0) AS io_iprice, IF(io_input = '2', io_price, 0) AS io_oprice,
IF(io_input = '1', io_price * io_quan, 0) AS io_itotal, IF(io_input = '2', io_price * io_quan, 0) AS io_ototal
FROM tbl_iolist WHERE io_seq = #{io_seq}

mapper.insert:
 	INSERT INTO tbl_iolist(
 		io_seq,	io_date,io_time,io_input,io_pname,io_price,io_quan,	io_total
	) VALUES( 
		#{io_seq},#{io_date},#{io_time},#{io_input},#{io_pname},#{io_price},#{io_quan},#{io_total}
	)
	ON DUPLICATE KEY UPDATE
		io_date = #{io_date},
		io_time = #{io_time},
		io_input = #{io_input},
		io_pname = #{io_pname},
		io_price = #{io_price},
		io_quan = #{io_quan},	
		io_total = #{io_total}
